const changeTheNumber = (state = { counter: 0 }, action) => {

    switch (action.type) {
        case "INCREMENTBY5": return { counter: state.counter + action.payload }
        case "INCREMENT": return { counter: state.counter + 1 }
        case "DECREMENT": return { counter: state.counter - 1 }
        case "toggle": return { counter: state.counter }
        default: return { counter: state.counter }
    };
}
export default changeTheNumber;
