const initialState1 = 0;

const changeTheNumber = (state = initialState1, action) => {

    switch (action.type) {
        case "INCREMENTBY5": {
            return {
                state:state + action.payload,
            }
        }
        case "INCREMENT": {
            return {
                state: state + 1
            }
        }
        case "DECREMENT": {
            return {
                state:state - 1
            }
        }
        case "toggle": {
            return {
                state
            }
        }
        default: return state;
    }

}
export default changeTheNumber;
