const changeTheNumber = (state = {counter:0}, action) => {

    switch (action.type) {
        case "INCREMENTBY5": return counter + action.payload;
        case "INCREMENT": return counter + 1;
        case "DECREMENT": return counter - 1;
        case "toggle": return counter;
        default: return counter;
    };
}
    export default changeTheNumber;
