const changeTheNumber = (state = {counter:0}, action) => {

    switch (action.type) {
        case "INCREMENTBY5": return {counter:counter + action.payload }
        case "INCREMENT": return {counter:counter + 1}
        case "DECREMENT": return {counter:counter - 1}
        case "toggle": return {counter:counter}
        default: return {counter:counter}
    };
}
    export default changeTheNumber;
