import classes from './Auth.module.css';
import { useState } from 'react';
import './cursor.css'

const Auth = () => {

  const [arr, setarr] = useState('Register');
  const [arr1, setarr1] = useState('Already have account');
  const [arr2, setarr2] = useState(true);
  const [email, setemail] = useState('');
  const [pass, setpass] = useState('');

  const fun2 = () => {
    var obj = {
      type: arr,
      email,
      password: pass
    }
    console.log(obj);
  }

  const fun = () => {
    if (arr1 === 'Already have account') {
      setarr("Login");
      setarr1("Dont have acount");
    } else {
      setarr("Register");
      setarr1("Already have account");
    }
    setarr2(!arr2);
  }
  return (
    <main className={classes.auth}>
      <section>
          <div className={classes.control}>
            <label htmlFor='email'>Email</label>
            <input type='email' onChange={(e) => setemail(e.target.value)} id='email' />
          </div>
          <div className={classes.control}>
            <label htmlFor='password'>Password</label>
            <input type='password' onChange={(e) => setpass(e.target.value)} id='password' />
          </div>
          {arr2 && <div className={classes.control}>
            <label htmlFor='password'>Confirm Password</label>
            <input type='password' id='passwordconfirm' />
          </div>}
          <button onClick={() => fun2()} >{arr}</button>
          <ul className="pointer">
            <p onClick={() => fun()}>{arr1} </p>
          </ul>
      </section>
    </main>
  );
};

export default Auth;
