const initialState1 = 0;

const changeTheNumber = (state = initialState1, action) => {

    switch (action.type) {
        case "INCREMENTBY5": {
            return {
                initialState: state.initialState + action.payload,
            }
        }
        case "INCREMENT": {
            return {
                initialState: state.initialState + 1
            }
        }
        case "DECREMENT": {
            return {
                initialState: state.initialState - 1
            }
        }
        case "toggle": {
            return {
                Toggle:!state.Toggle
            }
        }
        default: return state;
    }

}
export default changeTheNumber;
