const initialState1 = { initialState:0 ,Toggle:true };

const changeTheNumber = (state = initialState1, action) => {
    switch (action.type) {
        case "INCREMENTBY5": return state + action.payload;
        case "INCREMENT": return state.initialState + 1;
        case "DECREMENT": return state.initialState - 1;
        case "toggle": return !state.Toggle;
        default: return state;
    }

}
export default changeTheNumber;
