const adder=0;

const changeTheNumber = (state =adder, action) => {

    switch (action.type) {
        case "INCREMENTBY5": return { adder: state.adder + action.payload };
        case "INCREMENT": return { adder: state.adder + 1 };
        case "DECREMENT": return { adder: state.adder - 1 };
        case "toggle": return { adder: state.adder };
        default: return { adder: state.adder };
    };
}
export default changeTheNumber;
