      type: arr,
      email,
      password: pass
    }
    if (obj.type === 'Login') {
      console.log(obj);
      await axios.post(`${ip}/login`, obj).then((res) => {
        console.log(res.data)
      })
    }
    else {
      await axios.post(`${ip}/register`, obj)
